css:
*{
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: 'Josefin Sans', sans-serif;
}


.wrapper {
  width: 100%;
  height: auto;
}

.card {
  background: #fff;
  padding: 20px 20px 0;
  position: relative;
}

.info .left {
  width: 80%;
  display: inline-block;
}

.info .right {
  display: inline-block;
  width: 10%;
}

.info .left img{
  height: 50px;
  position: relative;
  left: 50px;
}
.info .left .header {
  text-align: center;
}
.info .left .header .vaccine-center {
  font-size: 12px;
}

.info .left .header .vaccination {
  margin-top: 2px;
  color: green;
  font-size: 12px
}

.info .left .header .vaccination-reminder {
  margin-top: 2px;
  font-size: 10px;
}

.info .left .header .id-no {
  margin-top: 5px;
  text-align: right;
  font-size: 16px;
}
.info .left .patient-info {
  margin-top: 1px;
  font-size: 14px;
}

.info .left .patient-info .first_name {
  font-size: 12px;
  text-align: center;
  margin-right: 20px;
}

.info .left .patient-info .last_name {
  font-size: 12px;
  text-align: center;
  margin-left: 10px;
  margin-right: 30px;
}
hr {
  margin-left: 10px;
}
.info .left .patient-info .middle_name {
  font-size: 12px;
  text-align: center;
  margin-left: 10px;
  margin-right: 20px;
}
.info .left .patient-info .suffix {
  font-size: 12px;
  text-align: center;
  margin-left: 10px;
}

.info .left .patient-info .date_of_birth {
  font-size: 12px;
  text-align: center;
  margin-left: 10px;
}
.info .left .patient-info .gender {
  font-size: 12px;
  margin-left: 50px;
}
.info .left .patient-info .category {
  font-size: 12px;
  margin-left: 50px;
}
.info .left .patient-info .date_of_birth_2 {
  font-size: 12px;
  margin-left: 10px;
  margin-right: 60px;
}

.info .left .patient-info .gender_2 {
  font-size: 12px;
  margin-right: 65px;
}

.info .left .patient-info .category_2 {
  font-size: 12px;
  margin-right: 20px;
}


.vaccine-details table td {
  border: solid 1px;
  padding: 3px;
}

.card .info{
  width: 100%;
  height: auto;
  border: solid 2px green;
  position: relative;
  bottom: 10px;
  right: 13px;
}

.info .right .patient-photo {
  border: solid 1px black;
  width: 100px;
  height: auto;
}
.info .right .truhealth-seal {
  width: 50px;
  height: 50px;
  text-align: center;
}

.info .right .qr-code {
  border: solid 1px black;
  width: 20px;
  height: 20px;
  margin-left: 10px;
}

.vaccinator {
  display: inline-block;
}

.vaccinator-signature {
  display: inline-block;
  position: absolute;
  bottom: 20px;
  z-index: 1
}

.signature {
  width: 20px;
}
